2cm; margin-bottom: 0cm;\"><br /></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font face=\"Courier New, monospace\">#include &lt;iostream&gt;<br />#include &lt;fstream&gt;<br />#include &lt;algorithm&gt;<br />#include &lt;sstream&gt;<br />using namespace std;<br /><br />template &lt;class T&gt;<br />inline std::string to_string (const T&amp; t)<br />{<br /> std::stringstream ss;<br /> ss &lt;&lt; t;<br /> return ss.str();<br />}<br /><br />int main()<br />{<br /> srand(time(0));<br /> for(int i=1;i&lt;31;++i)<br /> {<br /> int a=((int)((double)rand())/((double)RAND_MAX)*10000.0)+1;<br /> int b=((int)((double)rand())/((double)RAND_MAX)*100.0)+1;<br /> fstream fin((string(&quot;in&quot;)+to_string(i)).c_str(),fstream::out);<br /> fin &lt;&lt; a &lt;&lt; &quot; &quot; &lt;&lt; (b+a);<br /> fin.close();<br /> fstream fout((&quot;out&quot;+to_string(i)).c_str(),fstream::out);<br /> if (a%2==0)a+=1;<br /> fout &lt;&lt; a;<br /> for(int i=a+2;i&lt;=a+b;i+=2)<br /> {<br /> if (i%2==1) fout &lt;&lt; &quot; &quot; &lt;&lt; i;<br /> }<br /> fout.close();<br /> }<br /> return 0;<br />}<br /><br /></font></p> </p>',1228855024),(231,'2f3accef3cbfa593763bac7acb1390b5','<p> <style type=\"text/css\"> &amp;amp;lt;!-- @page { margin: 2cm } P { margin-bottom: 0.21cm } --&amp;amp;gt; </style><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><span style=\"font-weight: bold;\">Постановка задачи</span><br /></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\">Нужно вывести на экран нечетные чисел из отрезка от <span lang=\"en-US\">A</span> до <span lang=\"en-US\">B</span>.</p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><br /></p><p lang=\"en-US\" style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font size=\"4\"><b>Input</b></font></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\">Со стандартного устройства ввода вводятся 2 натуральных числа – <span lang=\"en-US\">A</span> и <span lang=\"en-US\">B</span>.1&lt;=<span lang=\"en-US\">A</span>&lt;=<span lang=\"en-US\">B</span>&lt;300.</p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><br /></p><p lang=\"en-US\" style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font size=\"4\"><b>Output</b></font></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\">Нужно выдать на стандартное устройство вывода все требуемые чисел в порядке возрастания через пробел. В конце строки пробел ставить не следует.</p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><br /></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font size=\"4\"><span lang=\"en-US\"><b>Sample</b></span></font><font size=\"4\"><span lang=\"en-US\"><b> Input</b></span></font></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font face=\"Courier New, monospace\">6 13</font></p><p lang=\"en-US\" style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><br /></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font size=\"4\"><span lang=\"en-US\"><b>Sample</b></span></font><font size=\"4\"><span lang=\"en-US\"><b> Output</b></span></font></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">7 9 11</span></font><font face=\"Courier New, monospace\"> 13</font></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"></p></p>',1229269304),(232,'ebfb65e8b73663f9fcfe4d699bc535a2','<p> <style type=\"text/css\"> &amp;lt;!-- @page { margin: 2cm } P { margin-bottom: 0.21cm } --&amp;gt;</style><span style=\"font-weight: bold;\">Постановка задачи</span><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\">Найти сумму двух «длинных» чисел. Т.е. чисел с большим количеством знаков.</p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><br /></p><p lang=\"en-US\" style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font size=\"4\"><b>Input</b></font></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\">Со стандартного устройства ввода вводятся в первой строке 2 натуральных числа, не превышающих 1000 – длины первого и второго чисел соответственно. Затем во второй строке идет первое число, в третьей – второе число.</p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><br /></p><p lang=\"en-US\" style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font size=\"4\"><b>Output</b></font></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\">Нужно выдать на стандартное устройство сумму этих двух чисел – так же длинное число.</p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><br /></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font size=\"4\"><span lang=\"en-US\"><b>Sample</b></span></font><font size=\"4\"><span lang=\"en-US\"><b> Input</b></span></font></p><p lang=\"en-US\" style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font face=\"Courier New, monospace\">17 17</font></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font face=\"Courier New, monospace\">56356356456456456</font></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font face=\"Courier New, monospace\">87987987897897987</font></p><p lang=\"en-US\" style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><br /></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font size=\"4\"><span lang=\"en-US\"><b>Sample</b></span></font><font size=\"4\"><span lang=\"en-US\"><b> Output</b></span></font></p><p lang=\"en-US\" style=\"margin-left: 0.32cm; margin-bottom: 0cm;\"><font face=\"Courier New, monospace\">144344344354354443</font></p><p lang=\"en-US\" style=\"margin-left: 0.32cm; margin-bottom: 0cm;\"><br /></p><p lang=\"en-US\" style=\"margin-left: 0.32cm; margin-bottom: 0cm;\"><font face=\"Courier New, monospace\">#include &lt;iostream&gt; <br />#include &lt;fstream&gt; <br />#include &lt;algorithm&gt; <br />#include &lt;sstream&gt; <br />using namespace std; <br /><br />template &lt;class T&gt;<br />inline std::string to_string (const T&amp; t)<br />{ <br /> std::stringstream ss; <br /> ss &lt;&lt; t; <br /> return ss.str(); <br />} <br /><br />string sum(string a1,string a2)<br />{<br /> a1=&quot;0&quot;+a1;<br /> a2=&quot;0&quot;+a2;<br /> while(a1.size()&lt;a2.size()) a1=&quot;0&quot;+a1;<br /> while(a1.size()&gt;a2.size()) a2=&quot;0&quot;+a2;<br /> string res=&quot;&quot;;<br /> int sp=0;<br /> for(int i=a1.size()-1;i&gt;=0;--i)<br /> {<br /> int ss = a1[i]+a2[i]-2*\'0\'+sp;<br /> char c[2];c[0]=((char)\'0\'+ss%10);c[1]=0;<br /> res=string(c)+res;<br /> sp=ss/10;<br /> }<br /> while (res[0]==\'0\') res.erase(0,1);<br /> return res;<br />}<br /><br />int main()<br />{<br /> srand(time(0));<br /> for(int i=1;i&lt;31;++i)<br /> {<br /> string ss1,ss2;<br /> for(int k=0;k&lt;30;++k)<br /> {<br /> int a=((int)((double)rand())/((double)RAND_MAX)*10000.0)+1;<br /> ss1 += to_string(a);<br /> }<br /> for(int k=0;k&lt;30;++k)<br /> {<br /> int a=((int)((double)rand())/((double)RAND_MAX)*10000.0)+1;<br /> ss2 += to_string(a);<br /> }<br /> fstream fin((string(&quot;in&quot;)+to_string(i)).c_str(),fstream::out);<br /> fin &lt;&lt; ss1.size() &lt;&lt; &quot; &quot; &lt;&lt; ss2.size() &lt;&lt; &quot;\\n&quot;;<br /> fin &lt;&lt; ss1 &lt;&lt; &quot;\\n&quot; &lt;&lt; ss2;<br /> fin.close();<br /> fstream fout((&quot;out&quot;+to_string(i)).c_str(),fstream::out);<br /> fout &lt;&lt; sum(ss1,ss2);<br /> fout.close();<br /> }<br /> return 0;<br />}<br /><br /></font></p> </p>',1228855073),(233,'29b6b9718287c576712e7975808e3caf','<p> <style type=\"text/css\"> &amp;amp;lt;!-- @page { margin: 2cm } P { margin-bottom: 0.21cm } --&amp;amp;gt;</style><span style=\"font-weight: bold;\">Постановка задачи</span><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\">Найти сумму двух «длинных» чисел. Т.е. чисел с большим количеством знаков.</p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><br /></p><p lang=\"en-US\" style=\"text-indent: 0.32cm; margin-bottom: 0cm;\"><font size=\"4\"><b>Input</b></font></p><p style=\"text-indent: 0.32cm; margin-bottom: 0cm;\">Со стандартного устройства ввода вводятся в первой строке 2 натуральных числа, не превышающих 1000 – длины первого и вто